mapper.select:
SELECT 
		 FROM sys_dept_role t 



		ORDER BY ${order}


SELECT * FROM ( SELECT 


		Row_Number() OVER(ORDER BY t.createDate desc) as RowNumber,
		Row_Number() OVER(ORDER BY ${order}) as RowNumber,




		${column} 

	t.id , t.createDeptId , t.createDeptCode , t.createDeptName , t.createAreaId , t.createAreaCode , t.createAreaName , t.createUserID , t.createUserCode , t.createUserName , t.createDate , t.updateUserID , t.updateUserCode , t.updateUserName , t.updateDate , t.rowOrder , t.remark , t.state , t.seqtable , t.seqlead , t.seqvalue
 FROM sys_dept_role t


	 ) as a WHERE RowNumber between #{firstResult,jdbcType=INTEGER}+1 and #{firstResult,jdbcType=INTEGER}+#{pageSize,jdbcType=INTEGER}
